#pragma config(Sensor, dgtl1,  ledTank,        sensorLEDtoVCC)
#pragma config(Sensor, dgtl2,  ledArcade,      sensorLEDtoVCC)
#pragma config(Motor,  port2,           leftFront,     tmotorVex393_MC29, openLoop, reversed)
#pragma config(Motor,  port3,           rightFront,    tmotorVex393_MC29, openLoop, reversed)
#pragma config(Motor,  port4,           leftBack,      tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port5,           rightBack,     tmotorVex393_MC29, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#pragma platform(VEX2)
#pragma competitionControl(Competition)
#include "Vex_Competition_Includes.c"

void pre_auton(){
}

task autonomous(){
}

task usercontrol(){
	bool useTank = true;

	if(!useTank){
		turnLEDOn(dgtl2);
		turnLEDOff(dgtl1);
	}else{
		turnLEDOn(dgtl1);
		turnLEDOff(dgtl2);
	}

	while(true){
		if(vexRT[Btn7U]){
			if(useTank){
				useTank = false;
				turnLEDOn(dgtl2);
				turnLEDOff(dgtl1);
			}else{
				useTank = true;
				turnLEDOn(dgtl1);
				turnLEDOff(dgtl2);
			}
			wait1Msec(500);
		}
		if(useTank){
			motor[port2] = vexRT[Ch3];
			motor[port3] = -vexRT[Ch2];
			motor[port4] = vexRT[Ch3];
			motor[port5] = -vexRT[Ch2];
		}else{
			int joy_x = vexRT[Ch1];
			int joy_y = vexRT[Ch3];
			int joy_threashold = 10;

			if((abs(joy_y) > joy_threashold) && (joy_y>0)){
				motor[port2] = joy_y;
				motor[port3] = joy_y;
				motor[port4] = joy_y;
				motor[port5] = joy_y;
			}else if((abs(joy_y) > joy_threashold) && (joy_y<0)){
				motor[port2] = joy_y;
				motor[port3] = joy_y;
				motor[port4] = joy_y;
				motor[port5] = joy_y;
			}else if((abs(joy_x) > joy_threashold) && (joy_x>0)){
				motor[port2] = joy_x;
				motor[port3] = joy_x;
				motor[port4] = joy_x;
				motor[port5] = joy_x;
			}else if((abs(joy_x) > joy_threashold) && (joy_x<0)){
				motor[port2] = joy_x;
				motor[port3] = joy_x;
				motor[port4] = joy_x;
				motor[port5] = joy_x;
			}else{
				motor[port2] = 0;
				motor[port3] = 0;
				motor[port4] = 0;
				motor[port5] = 0;
			}
		}
	}
}
